import { SET_GRID_SIZE } from "../actions/types";

const initialState = {
	height: 250,
	width: 250,
	modal: {
		active: false,
		type: "request",
		title: "Lets Get This Park Started"
	}
};

export default function(state = initialState, action) {
	switch (action.type) {
		case SET_GRID_SIZE:
			console.log(action);
			return {
				...state,
				height: action.payload.height,
				width: action.payload.width
			};
		case TOGGLE_MODAL:
			return {
				...state,
				modal: action.payload.modal
			};
		default:
			return state;
	}
}
