import React, { Component } from "react";
import ReactDOM from "react-dom";

import FontPicker from "font-picker-react";

import { saveFont } from "../actions/designActions";
import { connect } from "react-redux";

import "react-tabs/style/react-tabs.css";
import "../styles/DesignSettings.css";

class TextEditor extends Component {
	constructor(props) {
		super(props);
		this.state = {
			activeFontFamily: "Open Sans"
		};
	}

	handleChange = (color, event, name) => {
		console.log(name);
		this.props.saveColor(name, color.hex);
	};

	render() {
		return (
			<div className="design-details-container ">
				<h3> Add Some Text</h3>
				<input
					type="text"
					className="text-editor-input"
					placeholder="Enter Your Text Here"
				/>
				<FontPicker
					apiKey="AIzaSyBUSf-r50-odn9JVFU4k4n8nl_fJJ3GBtY"
					activeFontFamily={this.state.activeFontFamily}
				/>
				<p className="apply-font">
					The font will be applied to this text.
				</p>
			</div>
		);
	}
}

const mapStateToProps = state => ({
	design: state.design
});

export default connect(
	mapStateToProps,
	{ saveFont }
)(TextEditor);
