import axios from "axios";
import { SET_SOCK_COLOR } from "../actions/types";

const initialState = {
	hex: "",
	red: "",
	blue: "",
	green: ""
};

export default function(state = initialState, action) {
	switch (action.type) {
		case SET_SOCK_COLOR:
			return {
				...state,
				hex: action.payload.hex,
				red: action.payload.red,
				blue: action.payload.blue,
				green: action.payload.green
			};

		default:
			return state;
	}
}
