import React, { Component } from "react";
import ReactDOM from "react-dom";
import ObjectList from "./ObjectList.js";
import GridObjectItem from "./gridObjectItem.js";
import ComponentCounter from "./ComponentCounter.js";
import Modal from "./Modal.js";
import { connect } from "react-redux";
import { logoutUser } from "../actions/authActions";
import { toggleModal } from "../actions/gridActions";
import html2canvas from "html2canvas";
import Navbar from "./NavBar.js";
import "../styles/App.css";

class GridLayout extends Component {
	state = {
		draggedItem: [],
		addedComponents: [],
		counter: 0,
		componentX: 0,
		componentY: 0,
		movingComponentId: "",
		gridWidth: 0,
		gridHeight: 0,
		scale: 0,
		activeItem: 0,
		gridSize: "28px 28px",
		counterList: "",
		gridX: "",
		gridY: "",
		imgData: ""
	};

	componentDidMount() {
		//this.setGridSize();
		this.findGridSize();
		this.setSize();
		this.loadGrid();
	}

	componentDidUpdate(prevProps) {
		if (
			prevProps.grid.width !== this.props.grid.width ||
			prevProps.grid.height !== this.props.grid.height
		) {
			this.setSize();
			//this.setGridSize();
		}
	}

	findGridSize = () => {
		let screenWidth = this.refs["gridContainer"].offsetWidth;
		let gridWidth = Math.round((screenWidth * 0.8) / 100) * 100;
		let screenHeight = this.refs["gridContainer"].offsetWidth;
		let gridHeight = Math.round((screenHeight * 0.8) / 100) * 100;
		let scale = gridWidth / this.props.grid.width;
		this.setState({
			gridWidth: gridWidth,
			gridHeight: gridHeight,
			scale: scale
		});
	};

	gridIsSquare = (width, height) => {
		let sideLength;
		let scale;
		if (width > height) {
			sideLength = Math.round((height * 0.8) / 100) * 100;
		} else {
			sideLength = Math.round((width * 0.8) / 100) * 100;
		}
		scale = sideLength / this.props.grid.width;
		this.setState(
			{
				gridWidth: sideLength,
				gridHeight: sideLength,
				scale: scale
			},
			this.setGridSize(sideLength, this.props.grid.width)
		);
	};

	widthIsLarger = (width, height) => {
		let gridWidth = Math.round((width * 0.8) / 100) * 100;
		let scale = this.props.grid.width / this.props.grid.height;
		let gridHeight = gridWidth / scale;
		this.setState(
			{
				gridWidth: gridWidth,
				gridHeight: gridHeight,
				scale: scale
			},
			this.setGridSize(gridWidth, this.props.grid.width)
		);
	};

	heightIsLarger = (width, height) => {
		let gridHeight = Math.round((height * 0.8) / 100) * 100;
		let scale = this.props.grid.height / this.props.grid.width;
		let gridWidth = gridHeight / scale;
		this.setState(
			{
				gridWidth: gridWidth,
				gridHeight: gridHeight,
				scale: scale
			},
			this.setGridSize(gridHeight, this.props.grid.height)
		);
	};

	setSize = () => {
		const dimensions = this.refs["gridContainer"].getBoundingClientRect();
		let screenWidth = dimensions.width;
		let screenHeight = dimensions.height;
		if (this.props.grid.width === this.props.grid.height) {
			this.gridIsSquare(screenWidth, screenHeight);
		} else if (this.props.grid.width > this.props.grid.height) {
			this.widthIsLarger(screenWidth, screenHeight);
		} else if (this.props.grid.width < this.props.grid.height) {
			this.heightIsLarger(screenWidth, screenHeight);
		}
	};

	onDragOver = ev => {
		ev.stopPropagation();
		ev.preventDefault();
	};

	onDrop = ev => {
		ev.preventDefault();
		let draggedItem = this.state.draggedItem;
		let gridCoords = ReactDOM.findDOMNode(
			this.refs["gridBox"]
		).getBoundingClientRect();
		this.getItemCoords(
			gridCoords.x,
			gridCoords.y,
			ev.clientX,
			ev.clientY,
			draggedItem.width,
			draggedItem.length
		);
		if (draggedItem.length === 0) {
			return; //preventing 'invisible' elements being added
		} else {
			this.setState({
				counter: this.state.counter + 1,
				draggedItem: [],
				addedComponents: [...this.state.addedComponents, draggedItem]
			});
		}
	};

	getItemCoords = (gridX, gridY, itemX, itemY, itemWidth, itemHeight) => {
		this.setState({
			gridX: gridX,
			gridY: gridY,
			componentX: itemX - gridX + parseInt(itemWidth),
			componentY: itemY - gridY + parseInt(itemHeight)
		});
	};

	itemIsDragging = (id, name, img, width, length) => {
		let pieceId = this.state.addedComponents.length;
		console.log(pieceId);
		let rotation = 0;
		this.setState({
			draggedItem: { pieceId, name, img, length, width, rotation }
		});
	};
	itemIsDropped = (evt, id, img, width, length) => {};

	saveGrid = () => {
		var grid = this.state.addedComponents;
		localStorage.setItem("gridElements", JSON.stringify(grid));
	};

	loadGrid = () => {
		var saved = JSON.parse(localStorage.getItem("gridElements"));
		console.log(saved);
		if (saved) {
			this.setState({
				addedComponents: saved
			});
		}
	};

	saveItems = (id, img, width, length, rotation) => {
		this.setState({
			addedComponents: [
				...this.state.components,
				[id, img, length, width, rotation]
			]
		});
	};
	onDragStart = (ev, id) => {
		this.setState({
			movingComponentId: id
		});
	};
	deleteComponent = id => {
		let toBeDeleted = id;
		let filteredItems = this.state.addedComponents.filter(function(e) {
			return e.pieceId !== toBeDeleted;
		});
		this.setState({
			addedComponents: filteredItems
		});
	};

	rotateComponent = () => {
		let toBeRotated = this.state.movingComponentId;
		let allComponents = this.state.addedComponents;
		let currentRotation = allComponents.find(e => {
			return e.pieceId === toBeRotated;
		});
		let newRotation =
			currentRotation.rotation < 360 ? currentRotation.rotation + 90 : 0;
		Object.assign(allComponents.find(e => e.pieceId === toBeRotated), {
			rotation: newRotation
		});
	};

	removeItem = name => {};

	saveActiveItem = id => {
		this.setState({
			activeItem: id
		});
	};
	setGridSize = (gridSize, inputSize) => {
		let backgroundGridSize = gridSize / (inputSize / 10);
		this.setState({
			gridSize: `${backgroundGridSize}px ${backgroundGridSize}px`
		});
	};
	onLogoutClick = e => {
		e.preventDefault();
		this.props.history.push("/");
		this.props.logoutUser(this.props.history);
	};

	hideModal = e => {
		e.preventDefault();
		const modal = {
			active: false,
			type: "",
			title: ""
		};
		this.props.toggleModal(modal);
	};
	saveList = items => {
		this.setState({
			counterList: items
		});
	};

	captureImage = () => {
		let element = document.getElementById("capture");
		html2canvas(element).then(canvas => {
			let imgData = canvas.toDataURL("image/jpeg");
			this.setState({
				imgData: imgData
			});
		});
	};

	render() {
		let size = {
			width: this.state.gridWidth,
			height: this.state.gridHeight,
			backgroundSize: this.state.gridSize
		};

		return (
			<div>
				<Navbar
					setSize={this.setSize}
					request={this.showRequestModal}
					help={this.showHelpModal}
					captureImage={this.captureImage}
				/>
				<Modal
					show={this.props.grid.modal.active}
					handleClose={this.hideModal}
					title={this.props.grid.modal.title}
					type={this.props.grid.modal.type}
					components={this.state.counterList}
					captureImage={this.state.imgData}
					name={this.props.auth.user.name}
					email={this.props.auth.user.email}
				/>
				<div className="builder-container">
					<img
						alt=""
						className="container-background"
						src="images/background.jpg"
					/>
					<a
						onClick={this.onLogoutClick}
						className="logout-button "
						href=""
					>
						Logout
					</a>
					<ObjectList
						saveLocation={this.saveLocation}
						itemIsDragging={this.itemIsDragging}
						saveItems={this.saveItems}
						itemIsDropped={this.itemIsDropped}
					/>

					<div
						id="capture"
						className="grid-container"
						ref="gridContainer"
						//onDrop={e => this.onDrop(e, "complete")}
					>
						<div className="width-distance">
							{this.props.grid.width} ft
						</div>
						<div className="grid-box" ref="gridBox" style={size}>
							<div className="height-distance">
								{this.props.grid.height} ft
							</div>
							<div
								className="grid"
								style={size}
								onDragOver={e => this.onDragOver(e)}
								onDrop={e => this.onDrop(e)}
							>
								{this.state.addedComponents.map((items, id) => {
									let num = this.state.addedComponents.length;
									let name = this.state.addedComponents.name;
									return (
										<GridObjectItem
											key={items.pieceId}
											pieceId={items.pieceId}
											img={items.img}
											width={items.width}
											height={items.length}
											onDragStart={this.onDragStart}
											gridWidth={this.state.gridWidth}
											gridHeight={this.state.gridHeight}
											saveGrid={this.saveGrid}
											xLocation={this.state.componentX}
											yLocation={this.state.componentY}
											rotate={items.rotation}
											scale={this.state.scale}
											saveActiveItem={this.saveActiveItem}
											currentlyActive={
												this.state.activeItem
											}
											deleteComponent={
												this.deleteComponent
											}
											gridX={this.state.gridX}
											gridY={this.state.gridY}
										/>
									);
								})}
							</div>
						</div>
						<div className="grid-note">
							Each Square is 10ft x 10ft
						</div>
					</div>
					<ComponentCounter
						addedComponents={this.state.addedComponents}
						saveList={this.saveList}
					/>
				</div>
			</div>
		);
	}
}

const mapStateToProps = state => ({
	auth: state.auth,
	grid: state.grid
});

export default connect(
	mapStateToProps,
	{ logoutUser, toggleModal }
)(GridLayout);
