import axios from "axios";
import {
	SET_PRIMARY_SOCK_COLOR,
	SET_SECONDARY_SOCK_COLOR,
	SET_SHAPE_COLOR,
	ADD_SHAPE,
	UPDATE_SHAPES,
	ADD_TEXT,
	PREVIEW_TEXT
} from "../actions/types";

const initialState = {
	primary: {
		hex: "#b91f1f",
		red: 185,
		green: 31,
		blue: 31
	},
	secondary: {
		hex: "#333fb4",
		red: 51,
		green: 63,
		blue: 180
	},
	shape: {
		hex: "#333fb4",
		red: 51,
		green: 63,
		blue: 180
	},
	counter: 0,
	inputShapes: [],
	sideText: [],
	previewText: {
		text: "",
		style: {}
	}
};

export default function(state = initialState, action) {
	switch (action.type) {
		case SET_PRIMARY_SOCK_COLOR:
			return {
				...state,
				primary: {
					hex: action.hex,
					red: action.red,
					blue: action.blue,
					green: action.green
				}
			};
		case SET_SECONDARY_SOCK_COLOR:
			return {
				...state,
				secondary: {
					hex: action.hex,
					red: action.red,
					blue: action.blue,
					green: action.green
				}
			};
		case SET_SHAPE_COLOR:
			return {
				...state,
				shape: {
					hex: action.hex,
					red: action.red,
					blue: action.blue,
					green: action.green
				}
			};
		case ADD_SHAPE:
			let count = state.counter;
			let id = `${action.id}${count}`;
			count = count + 1;
			return {
				...state,
				counter: count,
				inputShapes: [
					...state.inputShapes,
					{
						x: 10,
						y: 10,
						width: 100,
						height: 100,
						src: action.src,
						id: id
					}
				]
			};
		case UPDATE_SHAPES:
			return {
				...state,
				inputShapes: action.items
			};
		case ADD_TEXT:
			let num = state.counter;
			let pieceId = `${action.id}${num}`;
			return {
				...state,
				counter: num,
				sideText: [
					...state.sideText,
					{
						text: action.text,
						id: pieceId,
						style: action.style
					}
				]
			};
		case PREVIEW_TEXT:
			return {
				...state,
				previewText: {
					text: action.text,
					style: action.style
				}
			};

		default:
			return state;
	}
}
