import axios from "axios";
import { SET_DESIGN_COLOR, TOGGLE_MODAL } from "./types";

export const toggleModal = modalInfo => {
	return {
		type: TOGGLE_MODAL,
		payload: modalInfo
	};
};

export const saveColor = color => {
	return {
		type: SET_DESIGN_COLOR,
		payload: color
	};
};

export const quoteRequest = messageData => {
	axios.post("/api/mailer/send", messageData).then(response => {
		if (response.data.msg === "success") {
			alert("Message Sent.");
			//this.resetForm();
		} else if (response.data.msg === "fail") {
			alert("Message failed to send.");
		}
	});
};
