import React from "react";
import "./styles/App.css";
import Navbar from "./components/NavBar.js";
import GridLayout from "./components/NewGridLayout.js";
import Modal from "./components/Modal.js";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      height: 250,
      width: 250,
      show: false,
      modalContents: {
        type: "What",
        title: "This Is a Message"
      }
    };
  }

  showModal = () => {
    this.setState({ show: true });
  };

  hideModal = () => {
    this.setState({ show: false });
  };

  setSize = (height, width) => {
    this.setState({
      height: parseInt(height),
      width: parseInt(width)
    });
  };
  showRequestModal = (type, message) => {
    this.setState({
      modalContents: {
        type: "request",
        title: "Lets Get This Park Started"
      }
    });
    this.showModal();
  };
  showHelpModal = (type, message) => {
    this.setState({
      modalContents: {
        type: "help",
        title: "Unsure of where to start?"
      }
    });
    this.showModal();
  };


  render() {
    return (
      <div className="App">
        <Navbar
          setSize={this.setSize}
          request={this.showRequestModal}
          help={this.showHelpModal}
        />
        <Modal
          show={this.state.show}
          handleClose={this.hideModal}
          title={this.state.modalContents.title}
          type={this.state.modalContents.type}
        />

        <GridLayout width={this.state.width} height={this.state.height} />
      </div>
    );
  }
}

export default App;
