// @flow
import React, { Component } from "react";
import { Stage, Layer, Text, Image, Circle, Group } from "react-konva";

export default class ShapeElement extends Component {
	$canvas: any;

	state = {
		isDragging: false,
		x: 50,
		y: 150,
		image: null,
		url: ""
	};

	componentDidMount() {
		this._initImage();
		// if (this.$canvas) {
		//   this._getUrl();
		// }
	}

	_setCanvasRef = node => {
		this.$canvas = node;
	};

	_onDragStart = () => {
		this.setState({
			isDragging: true
		});
	};

	_onDragEnd = event => {
		const { target } = event;
		const { attrs } = target;
		this.setState({
			isDragging: false,
			x: attrs.x,
			y: attrs.y
		});
	};

	_getUrl = () => {
		const url = this.$canvas.toDataURL();
		this.setState({ url });
	};

	_initImage = () => {
		const image = new window.Image();
		image.crossOrigin = "Anonymous";
		image.src =
			"https://images.pexels.com/photos/1183986/pexels-photo-1183986.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500";
		image.onload = () => {
			this.setState(
				{
					image
				},
				() => this._getUrl()
			);
		};
	};

	render() {
		const { url } = this.state;
		const { image } = this.state;
		return (
			<div>
				<Image
					x={150}
					y={100}
					draggable
					image={image}
					width={200}
					height={300}
				/>
				);
				<img src={url} alt="a" />
			</div>
		);
	}
}
