import axios from "axios";
import { SET_SOCK_COLOR } from "../actions/types";

const initialState = {
	sockColor: {
		hex: "",
		red: "",
		blue: "",
		green: ""
	}
};

export default function(state = initialState, action) {
	switch (action.type) {
		case SET_SOCK_COLOR:
			console.log(action.payload);
			return {
				...state,
				sockColor: { ...state.sockColor, red: action.payload.r }
			};

		default:
			return state;
	}
}
