import React, { Component } from "react";
import { render } from "react-dom";
import Portal from "./Portal";
import { Stage, Layer, Rect, Text, Image } from "react-konva";
import Konva from "konva";

import SockImage from "./SockImage";

import SockImage2 from "./SockImage2";
import { Socks } from "./constants";
import TextElement from "./TextElement";
import { addTextNode } from "./TextNode";

import { connect } from "react-redux";

class SidePreview extends Component {
	constructor(props) {
		super(props);
		this.state = {
			textItems: [
				{
					x: 10,
					y: 10,
					width: 100,
					height: 100,
					//fill: "red",
					id: "rect1"
				}
			],
			selectedShape: ""
		};
		const stageRef = React.createRef();
	}

	render() {
		// Stage is a div container
		// Layer is actual canvas element (so you may have several canvases in the stage)
		// And then we have canvas shapes inside the Layer
		const primary = this.props.drawing.primary;
		const secondary = this.props.drawing.secondary;
		const previewText = this.props.drawing.previewText;

		return (
			<div>
				<div className="primary-color">
					<Stage
						ref={this.stageRef}
						width={600}
						height={750}
						onMouseDown={e => {
							// deselect when clicked on empty area
							const clickedOnEmpty =
								e.target === e.target.getStage();
							console.log(e.target.getStage());
							if (clickedOnEmpty) {
								this.setState({
									selectedShape: null
								});
							}
						}}
					>
						<Layer>
							{/*<Portal>*/}

							{/*<Portal>*/}
							<SockImage2
								blue={primary.blue}
								red={primary.red}
								green={primary.green}
								width={500}
								height={694}
								src="images/socksideview4.png"
								shadowBlur={10}
							/>
							<SockImage2
								blue={secondary.blue}
								red={secondary.red}
								green={secondary.green}
								width={500}
								height={694}
								src="images/sideheeltoe.png"
								shadowBlur={0}
							/>
							<TextElement
								text={previewText.text}
								textProps={previewText.style}

								//shapeProps={data}
								/*isSelected={
									data.id === this.state.selectedShape
								}*/
								/*onSelect={() => {
									console.log(data.id);
									this.setState({
										selectedShape: data.id
									});
								}}*/
								/*onChange={newAttrs => {
									const inputs = this.state.inputList.slice();
									inputs[id] = newAttrs;
									this.setState({
										inputList: inputs
									});
								}}*/
							/>

							{this.state.textItems.map((data, id) => {
								console.log(data);
								return (
									<TextElement
										key={id}
										text=""
										shapeProps={data}
										isSelected={
											data.id === this.state.selectedShape
										}
										onSelect={() => {
											console.log(data.id);
											this.setState({
												selectedShape: data.id
											});
										}}
										onChange={newAttrs => {
											const inputs = this.state.textItems.slice();
											inputs[id] = newAttrs;
											this.setState({
												textItems: inputs
											});
										}}
									/>
								);
							})}
						</Layer>
					</Stage>
				</div>
				{/*<div className="secondary-color">
					<Stage width={800} height={800}>
						<Layer>
							<SockImage
								blue={color.blue}
								red={color.red}
								green={color.green}
								src={null}
							/>
						</Layer>
					</Stage>
				</div>*/}
			</div>
		);
	}
}

const mapStateToProps = state => ({
	drawing: state.drawing
});

export default connect(mapStateToProps)(SidePreview);
