import React from "react";
import axios from "axios";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { quoteRequest } from "../actions/gridActions";
import "../styles/App.css";

const RequestForm = ({ onSubmit }) => {
	return (
		<form className="modal-form" id="contact-form" onSubmit={onSubmit}>
			<div className="form-row input-field">
				<input type="text" placeholder="Name" id="name" />

				<input
					type="text"
					placeholder="Business Name"
					id="business-name"
				/>
			</div>
			<div className="form-row input-field">
				<input type="email" placeholder="Email Address" id="email" />
				<input type="tel" placeholder="Phone Number" id="phone" />
			</div>
			<div className="form-row input-field">
				<input type="text" placeholder="City, State" id="city" />
				<input type="text" placeholder="Country" id="country" />
			</div>
			<div className="form-row input-field">
				<textarea
					className="materialize-textarea"
					type="text"
					placeholder="Give us some details about what you need!"
					id="message"
					style={{
						height: "100px"
					}}
				/>
			</div>
			<button className="button hoverable" type="submit">
				Submit
			</button>
		</form>
	);
};

const HelpForm = ({ onClick }) => {
	return (
		<form className="modal-form">
			<div className="form-row input-field">
				<input type="text" placeholder="Name" id="name" />

				<input type="text" placeholder="Email" id="email" />
			</div>

			<div className="form-row input-field">
				<textarea
					className="materialize-textarea"
					type="text"
					placeholder="Please describe what happened, and what you expected to happen."
					id="message"
					style={{
						height: "200px"
					}}
				/>
			</div>
			<button className="button hoverable" type="submit">
				Submit
			</button>
		</form>
	);
};

class Modal extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			resonse: "",
			email: ""
		};
	}
	componentDidMount() {}
	handleSubmit = e => {
		e.preventDefault();
		const name = document.getElementById("name").value;
		const business = document.getElementById("business-name").value;
		const email = document.getElementById("email").value;
		const phone = document.getElementById("phone").value;
		const city = document.getElementById("city").value;
		const country = document.getElementById("country").value;
		const message = document.getElementById("message").value;
		const objects = this.state.components;
		let messageData = {
			name: name,
			business: business,
			email: email,
			phone: phone,
			location: city + " " + country,
			message: message,
			components: objects
		};
		axios({
			method: "POST",
			url: "/api/mailer/send",
			data: messageData
		}).then(response => {
			console.log(response);
			if (response.data.msg === "success") {
				alert("Message Sent.");
				this.resetForm();
			} else if (response.data.msg === "fail") {
				alert("Message failed to send.");
			}
		});
	};

	resetForm() {
		document.getElementById("contact-form").reset();
	}

	render() {
		let showHideClassName = this.props.show
			? "modal display-block"
			: "modal display-none";
		let form;
		let backgroundImage;
		if (this.props.type === "request") {
			backgroundImage = "images/modalbackground.jpg";
			form = <RequestForm onSubmit={this.handleSubmit.bind(this)} />;
		} else if (this.props.type === "help") {
			backgroundImage = "images/helpmodalbackground.jpg";
			form = <HelpForm onSubmit={this.props.handleClose} />;
		}
		return (
			<div className={showHideClassName} ref={this.props.reference}>
				<section className="modal-main">
					<img
						alt=""
						className="close-modal"
						src="images/close.png"
						onClick={this.props.handleClose}
					/>
					<img alt="" className="modal-background-dimmer" />
					<h2>{this.props.title}</h2>
					{form}
				</section>
			</div>
		);
	}
}

const mapStateToProps = state => ({
	auth: state.auth,
	errors: state.errors
});

export default connect(
	mapStateToProps,
	{ quoteRequest }
)(Modal);
