import React, { Component } from "react";
import { render } from "react-dom";

class TextElement extends Component {
	constructor(props) {
		super(props);
		this.state = {
			x: "",
			y: "",
			rotation: "",
			size: "",
			font: "",
			color: ""
		};
		this.inputRef = React.createRef();
	}

	render() {
		return (
			<React.Fragment>
				<input
					ref={this.inputRef}
					style={{
						position: "absolute",
						top: 100,
						left: 100,
						width: "200px"
					}}
					placeholder="DOM input from Konva nodes"
					draggable
				/>
			</React.Fragment>
		);
	}
}

export default TextElement;
