import React from "react";
import ReactDOM from "react-dom";
import Draggable from "react-draggable";
import DeleteComponent from "./DeleteComponent.js";
import RotateObject from "./RotateObject.js";
import { connect } from "react-redux";

class GridObjectItem extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			itemLocations: [],
			rotation: 0,
			scale: 1,
			active: false,
			xPos: "",
			yPos: "",
			location: "",
			defaultPosition: {}
		};
		this.itemRef = React.createRef();
	}

	componentDidMount(e) {
		this.setState({
			scale: this.props.scale,
			location: this.props.location
		});
		this.setDefaultPosition();
	}
	componentWillUpdate(nextProps) {
		if (nextProps.scale !== this.props.scale) {
			this.setState({ scale: nextProps.scale });
		}
		if (
			nextProps.currentlyActive !== this.props.pieceId &&
			this.state.active === true
		) {
			this.setState({
				active: false
			});
		}
		/*if (this.props.currentlyActive !== this.props.pieceId) {
			console.log("checking");
			console.log(this.state.active);
			this.setState({
				active: false
			});
		}*/
	}

	rotateObject = () => {
		let rotation = this.state.rotation;
		let newRotation = rotation + 90;
		this.setState({
			rotation: newRotation
		});
	};

	deleteObject = id => {
		this.props.deleteComponent(id);
	};

	onDragStart = (ev, id) => {
		let location = this.getItemLocation();
		//this.props.saveGrid(id, location);
		//this.selectElement(ev, this.props.pieceId);
		this.props.onDragStart(ev, id);
	};

	onDragStop = (ev, id) => {
		console.log("drag stopped");

		/*if (this.state.active === true) {
			this.setState({ active: false });
		}*/
	};

	getItemLocation = () => {
		let gridLoc = ReactDOM.findDOMNode(this.refs["gridItem"]).style
			.transform;

		return gridLoc;
	};

	handleStop = (ev, width, height) => {
		//let position = this.refs["gridBox"].getBoundingClientRect();
		this.setState({
			locationX: ev.clientX - this.props.gridX - this.props.width,
			locationY: ev.clientY - this.props.gridY - this.props.height
		});
		//this.props.saveLocation(left, top, right, bottom);
	};

	selectElement = (ev, id) => {
		//this.props.saveActiveItem(id);
		//if (this.props.currentlyActive == id) {
		if (this.state.active === true) {
			this.props.saveActiveItem("");
			this.setState({ active: false });
		} else {
			this.props.saveActiveItem(id);

			this.setState({ active: true });
			this.props.saveActiveItem(id);

			//}
		}
	};

	removeActive = () => {
		if (this.props.currentlyActive !== this.props.pieceId) {
			this.setState({
				active: false
			});
		}
	};

	setDefaultPosition = () => {
		let itemWidth =
			(this.props.width / this.props.grid.width) * this.props.gridWidth;
		let itemHeight =
			(this.props.height / this.props.grid.height) *
			this.props.gridHeight;
		let x = this.props.xLocation - itemWidth;
		let y = this.props.yLocation - itemHeight;

		this.setState({
			defaultPosition: {
				x: x,
				y: y
			}
		});
	};
	saveState = (elem, id) => {
		console.log(elem);
		this.setState({
			elem
		});
		this.props.saveGrid(id, elem);
	};

	render() {
		let itemWidth =
			(this.props.width / this.props.grid.width) * this.props.gridWidth;
		let itemHeight =
			(this.props.height / this.props.grid.height) *
			this.props.gridHeight;
		let containerSize = {
			width: itemWidth + "px",
			height: itemHeight + "px",
			transform: "translate(123.2px, 98px)"
		};

		if (this.state.rotation === 90 || this.state.rotation === 270) {
			containerSize = {
				width: itemHeight + "px",
				height: itemWidth + "px"
			};
		}
		const size = {
			width: itemWidth + "px",
			height: itemHeight + "px",
			transform: `translate(-50%,-50%) rotate(${this.state.rotation}deg)`
		};
		//let defaultPosition = JSON.parse(this.state.defaultPosition);
		//console.log(defaultPosition);

		return (
			<Draggable
				bounds="parent"
				defaultPosition={this.state.defaultPosition}
				grid={[1, 1]}
				scale={1}
				onStart={ev => this.onDragStart(ev, this.props.pieceId)}
				onStop={(ev, data, id) => {
					this.saveState(
						{
							defaultPosition: { x: data.x, y: data.y }
						},
						this.props.pieceId
					);
				}}
			>
				<div
					ref={"gridItem"}
					className={`grid-item ${
						this.state.active === true ? "active" : ""
					}`}
					style={containerSize}
					onClick={ev => this.selectElement(ev, this.props.pieceId)}
				>
					<img
						className="object-img"
						style={size}
						alt=""
						draggable
						src={this.props.img}
					/>
					{this.state.active ? (
						<div className="object-actions">
							<DeleteComponent
								deleteComponent={() => {
									this.deleteObject(this.props.pieceId);
								}}
							/>
							<RotateObject rotateComponent={this.rotateObject} />
						</div>
					) : (
						""
					)}
				</div>
			</Draggable>
		);
	}
}

const mapStateToProps = state => ({
	auth: state.auth,
	grid: state.grid
});

export default connect(mapStateToProps)(GridObjectItem);
