import React from "react";
import { NavButton, NavInput } from "./constants.js";

class Navbar extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			height: "",
			width: ""
		};
		this.updateInput = this.updateInput.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}

	updateInput(id, event) {
		this.setState({ [id]: event.target.value });
	}

	onSubmit() {
		this.props.setSize(this.state.height, this.state.width);
	}

	openRequestModal = () => {
		this.props.request();
	};

	openHelpModal = () => {
		this.props.help();
	};

	render() {
		return (
			<div className="navbar">
				<div className="company-icon">
					<img src="images/Shock-logo.png" />
				</div>
				<div className="size-input">
					<NavInput
						placeholder={"Width"}
						onChange={e => this.updateInput("width", e)}
					/>
					<NavInput
						placeholder={"Height"}
						onChange={e => this.updateInput("height", e)}
					/>
					<NavButton name={"Submit"} onClick={this.onSubmit} />
				</div>
				<div className="submission-buttons">
					<NavButton
						name={"Request Quote"}
						onClick={this.openRequestModal}
					/>
					<NavButton name={"Help"} onClick={this.openHelpModal} />
				</div>
			</div>
		);
	}
}

export default Navbar;
