import axios from "axios";
import {
	SET_PRIMARY_SOCK_COLOR,
	SET_SECONDARY_SOCK_COLOR,
	SET_SHAPE_COLOR,
	ADD_SHAPE
} from "../actions/types";

const initialState = {
	primary: {
		hex: "#b91f1f",
		red: 185,
		green: 31,
		blue: 31
	},
	secondary: {
		hex: "#333fb4",
		red: 51,
		green: 63,
		blue: 180
	},
	shape: {
		hex: "#333fb4",
		red: 51,
		green: 63,
		blue: 180
	},
	inputShapes: {}
};

export default function(state = initialState, action) {
	switch (action.type) {
		case SET_PRIMARY_SOCK_COLOR:
			return {
				...state,
				primary: {
					hex: action.hex,
					red: action.red,
					blue: action.blue,
					green: action.green
				}
			};
		case SET_SECONDARY_SOCK_COLOR:
			return {
				...state,
				secondary: {
					hex: action.hex,
					red: action.red,
					blue: action.blue,
					green: action.green
				}
			};
		case SET_SHAPE_COLOR:
			return {
				...state,
				shape: {
					hex: action.hex,
					red: action.red,
					blue: action.blue,
					green: action.green
				}
			};
		case ADD_SHAPE:
			//let count = initialState.inputShapes.length;
			//let id = `${action.id}+${count}`;
			return {
				...state,
				inputShapes: {
					...state.inputShapes,
					x: 10,
					y: 10,
					width: 100,
					height: 100,
					src: action.src,
					id: action.id
				}
			};

		default:
			return state;
	}
}
