import React, { Component } from "react";
import Konva from "konva";
import { render } from "react-dom";
import { Stage, Layer, Rect, Image } from "react-konva";
import useImage from "use-image";

const URL = "images/WhiteSocks.png";

class SockImage extends React.Component {
	state = {
		color: "green"
	};
	componentDidMount() {
		this.applyCache();
		this.image = useImage(URL, "Anonimus");
		this.imageRef = React.useRef();
	}
	handleClick = () => {
		this.setState(
			{
				color: Konva.Util.getRandomColor()
			},
			() => {
				// recache shape when we updated it
				this.applyCache();
			}
		);
	};
	applyCache() {
		this.imageRef.cache();
		this.imageRef.getLayer().batchDraw();
	}
	render() {
		return (
			<Image
				filters={[Konva.Filters.RGB]}
				x={200}
				y={10}
				image={this.image}
				width={50}
				height={50}
				fill={this.state.color}
				blue={this.props.blue}
				red={this.props.red}
				green={this.props.green}
				shadowBlur={10}
				ref={this.imageRef}
				onClick={this.handleClick}
			/>
		);
	}
}

export default SockImage;
