import axios from "axios";
import { SET_SOCK_COLOR } from "./types";

export const setSockColor = color => {
	let colorCodes = {
		hex: color.hex,
		red: color.rgb.r,
		blue: color.rgb.b,
		green: color.rgb.g
	};
	return {
		type: SET_SOCK_COLOR,
		payload: colorCodes
	};
};
