import React, { Component } from "react";
import { connect } from "react-redux";

import "../styles/ProductPreview.css";

class TextPreview extends Component {
	constructor(props) {
		super(props);
		this.state = {
			hidden: true
		};
	}

	componentDidUpdate(prevProps) {
		if (this.props.design.logo !== "") {
			this.setState({
				hidden: false
			});
		}
	}

	render() {
		let logo = this.props.design.logo;
		console.log(logo);

		return (
			<div className="logo-preview-container grabable">
				<img
					className={this.state.hidden === true ? "deactivated" : ""}
					style={{ maxWidth: "200px" }}
					src={logo}
				/>
			</div>
		);
	}
}

const mapStateToProps = state => ({
	design: state.design
});

export default connect(mapStateToProps)(TextPreview);
