import React from "react";
import { NavButton, NavInput } from "./constants.js";
import { connect } from "react-redux";
import { setGridSize, toggleModal } from "../actions/gridActions";

class Navbar extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			height: "",
			width: ""
		};
		this.updateInput = this.updateInput.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}

	updateInput(id, event) {
		this.setState({ [id]: event.target.value });
	}

	onSubmit = e => {
		e.preventDefault();
		const dimensions = {
			height: this.state.height,
			width: this.state.width
		};
		this.props.setGridSize(dimensions);
	};

	openRequestModal = e => {
		e.preventDefault();
		const modal = {
			active: true,
			type: "request",
			title: "Lets Get This Park Started!"
		};
		this.props.toggleModal(modal);
	};

	openHelpModal = e => {
		e.preventDefault();
		const modal = {
			active: true,
			type: "help",
			title: "Report Bug"
		};
		this.props.toggleModal(modal);
	};

	render() {
		return (
			<div className="navbar">
				<div className="company-icon">
					<img alt="" src="images/Shock-logo.png" />
				</div>
				<div className="size-input input-field">
					<NavInput
						placeholder={"Width"}
						onChange={e => this.updateInput("width", e)}
					/>
					<NavInput
						placeholder={"Height"}
						onChange={e => this.updateInput("height", e)}
					/>
					<NavButton name={"Submit"} onClick={this.onSubmit} />
				</div>
				<div className="submission-buttons">
					<NavButton
						name={"Request Quote"}
						onClick={this.openRequestModal}
					/>
					<NavButton name={"Help"} onClick={this.openHelpModal} />
				</div>
			</div>
		);
	}
}

const mapStateToProps = state => ({
	width: state.width,
	height: state.height
});

export default connect(
	mapStateToProps,
	{ setGridSize, toggleModal }
)(Navbar);
