// @flow
import React, { Component } from "react";
import { Stage, Layer, Text, Image, Circle, Group } from "react-konva";

export default class ShapeElement extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isDragging: false,
			x: 50,
			y: 150,
			image: null,
			url: ""
		};
		this.itemRef = React.createRef();
		this.trRef = React.createRef();
	}
	componentDidMount() {
		this._initImage();
		// if (this.$canvas) {
		//   this._getUrl();
		// }
	}

	_onDragStart = () => {
		this.setState({
			isDragging: true
		});
	};

	_onDragEnd = event => {
		const { target } = event;
		const { attrs } = target;
		this.setState({
			isDragging: false,
			x: attrs.x,
			y: attrs.y
		});
	};

	_initImage = () => {
		const image = new window.Image();
		image.crossOrigin = "Anonymous";
		image.src = this.props.image;
		image.onload = () => {
			this.setState({
				image
			});
		};
	};

	render() {
		const { url } = this.state;
		const { image } = this.state;
		return (
			<React.Fragment>
				<Image
					onClick={this.props.onSelect}
					itemRef={this.itemRef}
					x={100}
					y={100}
					draggable
					image={image}
					width={100}
					height={100}
					onDragEnd={e => {
						this.props.onChange({
							//...shapeProps,
							x: e.target.x(),
							y: e.target.y()
						});
					}}
					onTransformEnd={e => {
						// transformer is changing scale
						const node = this.itemRef.current;
						const scaleX = node.scaleX();
						const scaleY = node.scaleY();

						// we will reset it back
						node.scaleX(1);
						node.scaleY(1);
						this.props.onChange({
							//...shapeProps,
							x: node.x(),
							y: node.y(),
							width: node.width() * scaleX,
							height: node.height() * scaleY
						});
					}}
				/>
				{this.props.isSelected && <Transformer ref={this.trRef} />}
			</React.Fragment>
		);
	}
}
