import React, { Component } from "react";
import ReactDOM from "react-dom";

//Component Imports
import Modal from "./Modal.js";
import ProductPreview from "./ProductPreview";
import DesignSettings from "./DesignSettings";
import Navbar from "./NavBar.js";

//Redux Imports
import { logoutUser } from "../actions/authActions";
import { toggleModal } from "../actions/gridActions";
import { connect } from "react-redux";

//style imports
import "../styles/App.css";
import "../styles/DesignLayout.css";

class DesignLayout extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentDidMount() {}

  componentDidUpdate(prevProps, prevState) {}

  onLogoutClick = e => {
    e.preventDefault();
    this.props.history.push("/");
    this.props.logoutUser(this.props.history);
  };

  hideModal = e => {
    e.preventDefault();
    const modal = {
      active: false,
      type: "",
      title: ""
    };
    this.props.toggleModal(modal);
  };

  render() {
    let size = {
      width: this.state.gridWidth,
      height: this.state.gridHeight,
      backgroundSize: this.state.gridSize
    };

    return (
      <div>
        <Navbar
          setSize={this.setSize}
          request={this.showRequestModal}
          help={this.showHelpModal}
          captureImage={this.captureImage}
        />
        <Modal
          show={this.props.grid.modal.active}
          handleClose={this.hideModal}
          title={this.props.grid.modal.title}
          type={this.props.grid.modal.type}
          components={this.state.counterList}
          captureImage={this.state.imgData}
          name={this.props.auth.user.name}
          email={this.props.auth.user.email}
        />
        <div className="builder-container">
          <img
            alt=""
            className="container-background"
            src="images/background.jpg"
          />
          <a onClick={this.onLogoutClick} className="logout-button " href="">
            Logout
          </a>
          <div className="row design-body">
            <div className="col s8 offset-s2">
              <ProductPreview />
            </div>
            <div className="col s8 offset-s2">
              <DesignSettings />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  grid: state.grid
});

export default connect(
  mapStateToProps,
  { logoutUser, toggleModal }
)(DesignLayout);
