import axios from "axios";
import {
	SET_PRIMARY_SOCK_COLOR,
	SET_SECONDARY_SOCK_COLOR,
	SET_SHAPE_COLOR,
	ADD_SHAPE
} from "./types";

export const setPrimarySockColor = color => {
	console.log(color.hex);
	return {
		type: SET_PRIMARY_SOCK_COLOR,
		hex: color.hex,
		red: color.rgb.r,
		blue: color.rgb.b,
		green: color.rgb.g
	};
};

export const setSecondarySockColor = color => {
	console.log(color.hex);
	return {
		type: SET_SECONDARY_SOCK_COLOR,
		hex: color.hex,
		red: color.rgb.r,
		blue: color.rgb.b,
		green: color.rgb.g
	};
};

export const setShapeColor = color => {
	console.log(color.hex);
	return {
		type: SET_SHAPE_COLOR,
		hex: color.hex,
		red: color.rgb.r,
		blue: color.rgb.b,
		green: color.rgb.g
	};
};

export const addShape = (src, name) => {
	return {
		type: ADD_SHAPE,
		src: src,
		id: name
	};
};

export const updateShapes = items => {
	return {
		type: UPDATE_SHAPES,
		items: items
	};
};
