import React from "react";
import Draggable from "react-draggable";
import DeleteComponent from "./DeleteComponent.js";
import RotateObject from "./RotateObject.js";
import { connect } from "react-redux";

class GridObjectItem extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			itemLocations: [],
			rotation: 0,
			scale: 1,
			active: false,
			locationX: "",
			locationY: ""
		};
	}

	componentDidMount(e) {
		this.setState({
			scale: this.props.scale
		});
	}
	componentWillReceiveProps(nextProps) {
		if (nextProps.scale !== this.props.scale) {
			this.setState({ scale: nextProps.scale });
		}
	}

	rotateObject = () => {
		let rotation = this.state.rotation;
		let newRotation = rotation + 90;
		this.setState({
			rotation: newRotation
		});
	};

	deleteObject = id => {
		console.log("DELETING OBJECT");
		this.props.deleteComponent(id);
	};

	onDragStart = (ev, id) => {
		this.props.onDragStart(ev, id);
	};

	handleStop = (ev, width, height) => {
		let position = this.refs["gridBox"].getBoundingClientRect();
		console.log(position);
		this.setState({
			locationX: ev.clientX - this.props.gridX - this.props.width,
			locationY: ev.clientY - this.props.gridY - this.props.height
		});
		//this.props.saveLocation(left, top, right, bottom);
	};

	selectElement = (ev, id) => {
		console.log("selecting" + id);
		this.props.saveActiveItem(id);
		//if (this.props.currentlyActive == id) {
		if (this.state.active === true) {
			this.setState({ active: false });
		} else {
			this.setState({ active: true });
			this.props.saveActiveItem(id);
			//}
		}
	};

	render() {
		let itemWidth =
			(this.props.width / this.props.grid.width) * this.props.gridWidth;
		let itemHeight =
			(this.props.height / this.props.grid.height) *
			this.props.gridHeight;
		//let rotation = this.state.rotation;
		let containerSize = {
			width: itemWidth + "px",
			height: itemHeight + "px"
		};
		if (this.state.rotation === 90 || this.state.rotation === 270) {
			containerSize = {
				width: itemHeight + "px",
				height: itemWidth + "px"
			};
		}
		const size = {
			width: itemWidth + "px",
			height: itemHeight + "px",
			transform: `translate(-50%,-50%) rotate(${this.state.rotation}deg)`
		};

		return (
			<Draggable
				bounds="parent"
				defaultPosition={{
					x: this.props.xLocation - itemWidth,
					y: this.props.yLocation - itemHeight
				}}
				position={{
					x: this.state.locationX,
					y: this.state.locationY
				}}
				grid={[1, 1]}
				scale={1}
				onStart={ev => this.onDragStart(ev, this.props.pieceId)}
				onDrag={this.onDrag}
				onStop={ev => this.handleStop(ev, itemWidth, itemHeight)}
			>
				<div
					ref={this.props.pieceId}
					className={`grid-item ${
						this.state.active === true ? "active" : ""
					}`}
					style={containerSize}
					onClick={ev => this.selectElement(ev, this.props.pieceId)}
				>
					<img
						className="object-img"
						style={size}
						alt=""
						draggable
						src={this.props.img}
					/>
					{this.state.active ? (
						<div className="object-actions">
							<DeleteComponent
								deleteComponent={() => {
									this.deleteObject(this.props.pieceId);
								}}
							/>
							<RotateObject rotateComponent={this.rotateObject} />
						</div>
					) : (
						""
					)}
				</div>
			</Draggable>
		);
	}
}

const mapStateToProps = state => ({
	auth: state.auth,
	grid: state.grid
});

export default connect(mapStateToProps)(GridObjectItem);
