import React, { Component } from "react";
import ReactDOM from "react-dom";

import { CirclePicker } from "react-color";

import { saveColor } from "../actions/designActions";
import { connect } from "react-redux";

import "../styles/DesignSettings.css";

class DesignSettings extends Component {
	constructor(props) {
		super(props);
		this.state = {};
	}

	handleChange = (color, event) => {
		this.props.saveColor(color.hex);
	};

	render() {
		return (
			<div className="design-settings-container">
				<CirclePicker onChange={this.handleChange} />
				{/* ## Color Picker
  				#Text Input
  				#Image Uploader
  			*/}
			</div>
		);
	}
}

const mapStateToProps = state => ({
	design: state.design
});

export default connect(
	mapStateToProps,
	{ saveColor }
)(DesignSettings);
