import React from "react";
import "../styles/App.css";

const RequestForm = ({ onSubmit }) => {
	return (
		<form className="modal-form" id="contact-form" onSubmit={onSubmit} method="POST">
			<div className="form-row">
				<input type="text" placeholder="Name" id="name" />
				<input type="text" placeholder="Business Name" id="business-name"/>
			</div>
			<div className="form-row">
				<input type="email" placeholder="Email Address" id="email"/>
				<input type="phone" placeholder="Phone Number" id="phone"/>
			</div>
			<div className="form-row">
				<input type="location" placeholder="City, State" id="city"/>
				<input type="location" placeholder="Country" id="country" />
			</div>
			<div className="form-row">
				<textarea
					type="textarea"
					placeholder="Give us some details about what you need!"
					id="message"
				/>
			</div>
			<button className="button" type="submit">
				Submit
			</button>
		</form>
	);
};

const HelpForm = ({ onClick }) => {
	return (
		<form className="modal-form">
			<div className="form-row">
				<div>
					<button className="button" onClick={onClick}>
						Yes!
					</button>
					<p>Show Me The Tutorial</p>
				</div>
				<div>
					<button className="button" onClick={onClick}>
						No Thanks!
					</button>
					<p>Take Me Back to the Builder</p>
				</div>
			</div>
		</form>
	);
};

class Modal extends React.Component {
	constructor(props) {
		super(props);
		this.state = {};
	}

	handleSubmit(e){
        e.preventDefault();
        const name = document.getElementById('name').value;
        const business = document.getElementById('business-name').value
        const email = document.getElementById('email').value;
        const phone = document.getElementById('phone').value;
        const city = document.getElementById('city').value;
        const country = document.getElementById('country').value;
        const message = document.getElementById('message').value;
        axios({
            method: "POST", 
            url:"http://localhost:3002/send", 
            data: {
                name: name,
                business: business,   
                email: email,
                phone: phone,
                city: city,
                country: country,  
                messsage: message
            }
        }).then((response)=>{
            if (response.data.msg === 'success'){
                alert("Message Sent."); 
                this.resetForm()
            }else if(response.data.msg === 'fail'){
                alert("Message failed to send.")
            }
        })
    }

	render() {
		let showHideClassName = this.props.show
			? "modal display-block"
			: "modal display-none";
		let form;
		let backgroundImage;
		if (this.props.type === "request") {
			backgroundImage = "images/modalbackground.jpg";
			form = <RequestForm onClick={this.handleSubmit.bind(this)} />;
		} else if (this.props.type === "help") {
			backgroundImage = "images/helpmodalbackground.jpg";
			form = <HelpForm onClick={this.props.handleClose} />;
		}
		return (
			<div className={showHideClassName} ref={this.props.reference}>
				<section className="modal-main">
					<img
						className="close-modal"
						src="images/close.png"
						onClick={this.props.handleClose}
					/>
					<img className="modal-background" src={backgroundImage} />
					<img className="modal-background-dimmer" />
					<h2>{this.props.title}</h2>
					{form}
				</section>
			</div>
		);
	}
}

export default Modal;
