import React, { Component } from "react";
import ReactDOM from "react-dom";

import FontPicker from "font-picker-react";

import ItemDetailSelector from "./ItemDetailSelector";

import { ChromePicker } from "react-color";

import { connect } from "react-redux";

class TextEditor extends Component {
	constructor(props) {
		super(props);
		this.state = {
			fontFamily: "Open Sans",
			text: "Example",
			fontAdded: false,
			style: {
				fontFamily: "Anton",
				fontSize: 20,
				//fontWeight: 200,
				//lineHeight: 20,
				fill: "#ffffff"
			},
			activated: false
		};
	}

	addFont = () => {
		this.setState({
			fontAdded: true
		});
	};

	addText = () => {
		console.log("text-added");
		this.props.addText("Insert Text", this.state.style);
	};

	componentDidUpdate(prevState) {
		console.log(prevState);
		console.lot(this.state);
		if (prevState !== this.state) {
			this.updateText();
		}
	}

	/*addText = event => {
		this.props.previewText(event.target.value, this.state.style);
	};*/
	updateText = e => {
		let style = this.state.style;
		let text = this.state.text;
		let id = this.props.selectedText[0].id;
		let data = this.props.data;
		console.log(id);
		let newAttrs = {
			text: text,
			style
		};
		let index = data.findIndex(obj => obj.id === id);
		let item = data.splice(index, 1);
		console.log(item);
		item[0] = {
			...item[0],
			text: text,
			style
		};
		data.push(item[0]);

		this.props.updateText(data);
	};

	render() {
		let selectedText = this.props.selectedText[0];
		//if (!selectedText) return null;
		console.log(selectedText);
		/*selectedText
			? this.setState({
					activated: true
			  })
			: this.setState({
					activated: false
			  });*/
		return (
			<div className="design-details-container text-editor ">
				<div className="row">
					<button
						className="add-text-button button hoverable col offset-s1"
						onClick={this.addText}
					>
						Add New Text
					</button>
				</div>
				{!selectedText ? null : (
					<React.Fragment>
						<div className="row">
							<div className="text input col s6">
								<label>Enter Your Text Here</label>
								<input
									type="text"
									className="text-editor-input apply-font"
									placeholder={selectedText.text}
									onChange={e =>
										this.setState({
											text: e.target.value
										})
									}
								/>
							</div>
							<div className="font-picker col s6">
								<label className=" col s12">
									Choose Your Font
								</label>
								<FontPicker
									apiKey="AIzaSyBUSf-r50-odn9JVFU4k4n8nl_fJJ3GBtY"
									activeFontFamily={
										this.state.style.fontFamily
									}
									onChange={nextFont =>
										this.setState({
											style: {
												...this.state.style,
												fontFamily: nextFont.family
											}
										})
									}
								/>
							</div>
						</div>
						<div className="row">
							<div className="col s6">
								<label>Choose Your Text Color</label>
								<ChromePicker
									onChangeComplete={this.onColorChange}
								/>
							</div>
							<div className="col s6">
								<div className="row">
									<div className="col s6">
										<label>Font Size</label>
										<input
											type="number"
											className="text-editor-input"
											placeholder={
												selectedText.style.fontSize
											}
											onChange={e =>
												this.setState({
													style: {
														...this.state.style,
														fontSize: e.target.value
													}
												})
											}
										/>
									</div>
									<div className="col s6">
										<label>Rotation</label>
									</div>
								</div>
								<div className="row">
									<div className="col s6">
										<label>Weight</label>
									</div>
									<div className="col s6">
										<label>Line Height</label>
									</div>
								</div>
							</div>
						</div>
					</React.Fragment>
				)}
			</div>
		);
	}
}

const mapStateToProps = state => ({
	drawing: state.drawing,
	side: state.side,
	bottom: state.bottom,
	top: state.top
});

export default connect(mapStateToProps)(TextEditor);
