import React, { Component } from "react";
import ReactDOM from "react-dom";

import { CirclePicker } from "react-color";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";

import { saveColor } from "../actions/designActions";
import { connect } from "react-redux";

import "react-tabs/style/react-tabs.css";
import "../styles/DesignSettings.css";

class DesignSettings extends Component {
	constructor(props) {
		super(props);
		this.state = {};
	}

	handleChange = (color, event) => {
		this.props.saveColor(color.hex);
	};

	render() {
		return (
			<div className="design-settings-container">
				<Tabs>
					<TabList>
						<Tab>Add Text</Tab>
						<Tab>Change Color</Tab>
						<Tab>Add Logo</Tab>
					</TabList>
					<TabPanel>
						<h2>Add Text</h2>
					</TabPanel>
					<TabPanel>
						<div className="color-picker-container">
							<h3>Primary Color</h3>
							<CirclePicker
								onChangeComplete={this.handleChange}
							/>
							<h3>Secondary Color</h3>
							<CirclePicker
								onChangeComplete={this.handleChange}
							/>
						</div>
					</TabPanel>
					<TabPanel>
						<h2>Add Logo</h2>
					</TabPanel>
				</Tabs>
				{/* ## Color Picker
  				#Text Input
  				#Image Uploader
  			*/}
			</div>
		);
	}
}

const mapStateToProps = state => ({
	design: state.design
});

export default connect(
	mapStateToProps,
	{ saveColor }
)(DesignSettings);
