import React, { Component } from "react";

import LogoSizeSelect from "./LogoSizeSelect";

import { saveLogo } from "../actions/designActions";
import { connect } from "react-redux";

class LogoUpload extends Component {
	constructor(props) {
		super(props);
		this.state = { file: null };
		this.onChange = this.onChange.bind(this);
		this.resetFile = this.resetFile.bind(this);
	}
	onChange(event) {
		this.setState({
			file: URL.createObjectURL(event.target.files[0])
		});
		this.props.saveLogo(URL.createObjectURL(event.target.files[0]));
	}

	resetFile(event) {
		event.preventDefault();
		this.setState({ file: null });
		this.props.saveLogo("");
	}
	render() {
		return (
			<div className="logo-upload-container">
				<div className="row">
					<div className="col s7">
						<input type="file" onChange={this.onChange} />
						{this.state.file && (
							<div style={{ textAlign: "center" }}>
								<button onClick={this.resetFile}>
									Remove File
								</button>
							</div>
						)}

						<div className="sample-image-container">
							<img
								style={{ maxWidth: "200px" }}
								src={this.state.file}
							/>
						</div>
					</div>

					<div className="col s5">
						<label>Logo Size</label>
						<LogoSizeSelect unit="x" type="LogoSize" />
						<label>Logo Rotation</label>
						<LogoSizeSelect unit="deg" type="rotation" />
					</div>
				</div>
			</div>
		);
	}
}

const mapStateToProps = state => ({
	design: state.design
});

export default connect(
	mapStateToProps,
	{ saveLogo }
)(LogoUpload);
