import axios from "axios";
import { SET_SOCK_COLOR } from "../actions/types";

const initialState = {
	color: {
		hex: "",
		red: "",
		green: "",
		blue: ""
	}
};

export default function(state = initialState, action) {
	switch (action.type) {
		case SET_SOCK_COLOR:
			return {
				...state,
				color: {
					hex: action.hex,
					red: action.red,
					blue: action.blue,
					green: action.green
				}
			};

		default:
			return state;
	}
}
